DROP TABLE IF EXISTS `admissions`;
CREATE TABLE `admissions` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `code` varchar(255) DEFAULT NULL COMMENT '学院代码',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '招生类型【1：硕士；2：博士】',
  `year` varchar(255) DEFAULT NULL COMMENT '年份【2020】',
  `discipline` varchar(255) DEFAULT NULL COMMENT '招生学科一级分类',
  `professional` varchar(255) DEFAULT NULL COMMENT '学科的专业及代码',
  `number` int(4) DEFAULT NULL COMMENT '招收人数',
  `totalscore` int(5) DEFAULT NULL COMMENT '总份数线',
  `score1` int(5) DEFAULT NULL COMMENT '科目一分数线',
  `score2` int(5) DEFAULT NULL COMMENT '科目二分数线',
  `score3` int(5) DEFAULT NULL COMMENT '科目三分数线',
  `score4` int(5) DEFAULT NULL COMMENT '科目四分数线',
  `score` int(5) DEFAULT NULL COMMENT '国控线',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;

