DROP TABLE IF EXISTS `faculty`;
CREATE TABLE `faculty` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `code` varchar(255) NOT NULL COMMENT '学校代码',
  `fenyuan` varchar(255) DEFAULT NULL,
  `yuanxi` varchar(255) DEFAULT NULL COMMENT '院系',
  `yuanxides` text COMMENT '院系介绍',
  `yuanxiurl` varchar(255) DEFAULT NULL COMMENT '院系网站地址',
  `laboratory` varchar(255) DEFAULT NULL COMMENT '实验室名称',
  `laboratorylevel` varchar(255) DEFAULT NULL COMMENT '实验室级别【文字描述：国家级重点科研基地、教育部重点科研基地】',
  `laboratoryurl` varchar(255) DEFAULT NULL COMMENT '实验室网站地址',
  `discipline` varchar(255) DEFAULT NULL COMMENT '一级学科及代码【学科名称[代码]】',
  `professional` text COMMENT '二级学科【专业】及代码，多科学以英文逗号分隔',
  `protype` int(1) NOT NULL DEFAULT '0' COMMENT '表示当前专业是否为博士专业[1|0]',
  `disciplinelevel` varchar(255) DEFAULT NULL COMMENT '学科级别【国家级重点学科】',
  `degree` varchar(255) DEFAULT NULL COMMENT '可授予的学位，多学位以英文逗号隔开',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

