DROP TABLE IF EXISTS `material`;
CREATE TABLE `material` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text CHARACTER SET utf8 NOT NULL COMMENT '文章内容',
  `subject` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '科目',
