  `date` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '创建时间',
  `url` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '资料外链',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

