-- ----------------------------
-- Table structure for number
-- ----------------------------
DROP TABLE IF EXISTS `number`;
CREATE TABLE `number` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `year` varchar(255) DEFAULT NULL COMMENT '年份【2020】',
  `code` varchar(255) DEFAULT NULL COMMENT '学校代码',
  `teacher` varchar(255) DEFAULT NULL COMMENT '专任教师',
  `dergraduate` varchar(255) DEFAULT NULL COMMENT '本科',
  `specialized` varchar(255) DEFAULT NULL COMMENT '专科',
  `master` varchar(255) DEFAULT NULL COMMENT '硕士研究生',
  `doctor` varchar(255) DEFAULT NULL COMMENT '博士研究生',
  `international` varchar(255) DEFAULT NULL COMMENT '留学生',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

