-- ----------------------------
-- Table structure for post
-- ----------------------------
DROP TABLE IF EXISTS `post`;
CREATE TABLE `post` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `type` varchar(255) DEFAULT NULL COMMENT '文章类型【gjzc、zsjz、jyjl、fsbk、tjzn, zz、yy、sx、zyk】',
  `title` varchar(255) DEFAULT NULL COMMENT '文章标题',
  `date` varchar(255) DEFAULT NULL COMMENT '文章日期【2020-11-13】',
  `origin` varchar(255) DEFAULT NULL COMMENT '文章来源/作者',
  `content` text NOT NULL COMMENT '文章内容',
  `readed` int(10) DEFAULT '0' COMMENT '文章阅读量',
  `url` varchar(255) DEFAULT NULL COMMENT '原文地址',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

